import React, {Component} from 'react';

export default class Header extends Component {
    state = ({currentScore: 4, highScore: 3});

    highScore = () => {
        if (this.state.highScore < this.state.currentScore) {
            this.setState({highScore: this.state.currentScore})
        } else if (this.state.highScore > this.state.currentScore) {
            this.setState({highScore: this.state.highScore})
        }
    }

    scoreIncrement = () => {
        this.setState({
            currentScore: this.state.currentScore + 1
        })
    }

    render() {
        return (
            <nav className="navbar navbar-expand-lg navbar-light bg-light">
                <a className="navbar-brand" href="#">Click Game</a>
                <div className="collapse navbar-collapse" id="navbarText">
                    <span className="navbar-text ml-auto">
                        <p onChange={this.scoreIncrement.bind(this)}>Current Score:  {this.state.currentScore}</p>
                        <p onChange={this.highScore.bind(this)}>High Score:  {this.state.highScore}</p>
                    </span>
                </div>
            </nav>
        )
    }

};
