import React, {Component} from 'react';

class Header extends Component {
    state = ({
        text: 'Header 1'
    });

    handleClick() {
        if(this.state === 'Header 1') {
            this.setState ({
                text: 'Header 2'
            })
        }
    }

    render() {
        return (
            <h1 onClick={this.handleClick}>{this.state.text}</h1>
        )
    }

};

export default Header;
